mapper.select:
select 
from SYS_USER_INFO 
WHERE 1=1 

 AND USER_ID=#{userId,jdbcType=DECIMAL} 


 AND USER_NAME=#{userName,jdbcType=VARCHAR} 

 
 AND USER_PWD=#{userPwd,jdbcType=VARCHAR}

SELECT USER_ID, USER_NAME, USER_REAL_NAME, USER_PWD, USER_PHONE, USER_DESC, SUI.ROLE_ID, SUI.STATUS, SUI.CREATE_DATE, SUI.MODIFY_DATE, SRI.ROLE_NAME FROM SYS_USER_INFO SUI LEFT JOIN SYS_ROLE_INFO SRI ON SUI.ROLE_ID=SRI.ROLE_ID WHERE 1 = 1
AND USER_NAME like #{userName,jdbcType=VARCHAR}
AND USER_NAME like #{userRealName,jdbcType=VARCHAR}
